import React, { useState } from 'react';
import axiosWithAuth from '../utils/axiosWithAuth';
import {
	Box,
	TextField,
	Button,
	Typography,
	List,
	ListItem,
	makeStyles,
} from '@material-ui/core';
import { useForm } from 'react-hook-form';

const initialColor = {
	color: '',
	code: { hex: '' },
	id: '',
};

const useStyles = makeStyles({
	container: {
		height: '100%',
		width: '300px',
		boxShadow: '0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23)',
	},
	heading: {
		fontSize: '3rem',
		marginTop: '4rem',
	},
	input: {
		width: '90%',
		display: 'block',
		margin: '30px auto',
	},
	button: {
		margin: '10px',
	},
	delete: {
		color: 'rgb(216, 87, 87)',
	},
	colorBox: {
		height: ' 16px',
		width: '16px',
		border: '1px lightgray solid',
		marginRight: '36px',
		marginLeft: '10px',
	},
});

const ColorList = ({ colors, updateColors }) => {
	// console.log(colors);
	const classes = useStyles();
	const [editing, setEditing] = useState(false);
	const [colorToEdit, setColorToEdit] = useState(initialColor);

	const editColor = (color) => {
		setEditing(true);
		setColorToEdit(color);
	};

	const saveEdit = async (e) => {
		try {
			e.preventDefault();
			const response = await axiosWithAuth().put(
				`/colors/${colorToEdit.id}`,
				colorToEdit
			);
			const filteredList = colors.filter(
				(color) => color.id !== colorToEdit.id
			);
			updateColors([...filteredList, response.data]);
			console.log(response);
		} catch (error) {
			alert(`Something went wrong => ${error}`);
		}
	};

	const deleteColor = async (color) => {
		try {
			const response = await axiosWithAuth().delete(`/colors/${color.id}`);
			const filteredList = colors.filter((color) => color.id !== response.data);
			updateColors([...filteredList]);
		} catch (error) {
			alert(`Something went wrong => ${error}`);
		}
	};

	return (
		<Box className={classes.container}>
			<Typography>colors</Typography>
			<List>
				{colors.map((color) => (
					<ListItem key={color.color} onClick={() => editColor(color)}>
						<Box component='span'>
							<Box
								className={classes.delete}
								component='span'
								onClick={(e) => {
									e.stopPropagation();
									deleteColor(color);
								}}
							>
								x
							</Box>{' '}
							{color.color}
						</Box>
						<Box
							className={classes.colorBox}
							style={{ backgroundColor: color.code.hex }}
						/>
					</ListItem>
				))}
			</List>
			{editing && (
				<Box component='form' onSubmit={saveEdit}>
					<legend>edit color</legend>
					<label>
						color name:
						<input
							onChange={(e) =>
								setColorToEdit({ ...colorToEdit, color: e.target.value })
							}
							value={colorToEdit.color}
						/>
					</label>
					<label>
						hex code:
						<input
							onChange={(e) =>
								setColorToEdit({
									...colorToEdit,
									code: { hex: e.target.value },
								})
							}
							value={colorToEdit.code.hex}
						/>
					</label>
					<Box>
						<Button variant='outlined' className={classes.button} type='submit'>
							save
						</Button>
						<Button
							variant='outlined'
							className={classes.button}
							onClick={() => setEditing(false)}
						>
							cancel
						</Button>
					</Box>
				</Box>
			)}
			<div className='spacer' />
			{/* stretch - build another form here to add a color */}
		</Box>
	);
};

export default ColorList;
