import React from 'react';
import { useHistory } from 'react-router-dom';
import { TextField, Box, Button, makeStyles } from '@material-ui/core';
import { useForm } from 'react-hook-form';
import axiosWithAuth from '../utils/axiosWithAuth';

const useStyles = makeStyles({
	container: {
		backgroundColor: 'rgba(211,199,221,0.9)',
		width: '50%',
		padding: '2%',
		margin: '10% auto',
		borderRadius: '15px',
	},
	heading: {
		fontSize: '3rem',
		marginTop: '4rem',
	},
	input: {
		width: '90%',
		display: 'block',
		margin: '30px auto',
	},
	button: {
		width: '40%',
		margin: '40px auto',
		display: 'block',
	},
});

const AddColor = ({ updateColors }) => {
	const classes = useStyles();
	const { handleSubmit, register, errors, formState, reset } = useForm({
		mode: 'onChange',
	});
	const history = useHistory();

	const onSubmit = async (data) => {
		try {
			const response = await axiosWithAuth().post('/colors', data);
			updateColors(response);
			reset();
			history.push('/bubbles');
		} catch (error) {
			alert(`Something went wrong => ${error}`);
		}
	};

	return (
		<Box>
			<Box
				component='form'
				onSubmit={handleSubmit(onSubmit)}
				className={classes.container}
			>
				<TextField
					className={classes.input}
					fullWidth={true}
					autoFocus
					variant='outlined'
					type='text'
					id='color'
					name='color'
					inputRef={register({ required: 'Required' })}
					label='color:'
					error={errors.color ? true : false}
					helperText={errors.color ? errors.color.message : null}
				/>
				<TextField
					className={classes.input}
					fullWidth={true}
					autoFocus
					variant='outlined'
					type='text'
					id='code.hex'
					name='code.hex'
					inputRef={register({ required: 'Required' })}
					label='Classification:'
					error={errors.code ? true : false}
					helperText={errors.code ? errors.code.message : null}
				/>

				<Button
					variant='contained'
					color='primary'
					size='large'
					type='submit'
					disabled={!formState.isValid}
					className={classes.button}
				>
					Submit
				</Button>
			</Box>
		</Box>
	);
};
export default AddColor;
